(function (module) {
    module
        .service('FormUtils', [
            FormUtils
        ]);

    function FormUtils() {

        var self = this;

        self.cleanModel = function cleanModel(model, fields) {
            fields = getArrayElementWithKey(fields);

            if(typeof model !== 'object'){
                return model;
            }

            Object.keys(model).forEach(function(key){
                if(!model.hasOwnProperty(key)){
                    return; //do nothing if it isn't own property
                }
                //if it's an empty string - remove it
                if(model[key] ===''){
                    delete model[key];
                    return;
                }
                //if it's an object, check fields
                var field = fields[key];
                if(field && field.hide){
                    delete model[key];
                    return;
                }
                if(typeof model[key] === 'object' && !Array.isArray(model[key])){
                    cleanModel(model[key], (field && field.fieldGroup) || fields);
                }
            });
            return model;
        };

        function getArrayElementWithKey(arr){
            if(typeof arr === 'undefined'){
                return {};
            }

            return Array.isArray(arr) ? arr.reduce(function(map, obj) {
                map[getKeyEnding(obj.key)] = obj;
                return map;
            }, {})
                : arr;
        }

        function getKeyEnding(str){
            var arr = str.split('.');
            return (arr && arr.length>0) ? arr[arr.length-1] : '';
        }
    }

    module
        .directive('isolateForm', [function () {
            return {
                restrict: 'A',
                require: '?form',
                link: function link(scope, element, iAttrs, formController) {

                    if (!formController) {
                        return;
                    }

                    // Remove this form from parent controller
                    formController.$$parentForm.$removeControl(formController);

                    var _handler = formController.$setValidity;
                    formController.$setValidity = function (validationErrorKey, isValid, cntrl) {
                        _handler(validationErrorKey, isValid, cntrl);
                        formController.$$parentForm.$setValidity(validationErrorKey, true, this);
                    };
                }
            };
        }]);
})(angular.module('sharedUtils'));

